mapper.select:
select
id, username, password, phone, role, create_time, update_time, delete_time, email, state, sex, avatar, graduated_school, open_id
from ars.user


        and id = #{id}


        and open_id = #{openId}


        and username = #{username}


        and password = #{password}


        and sex = #{sex}


        and avatar = #{avatar}


        and password = #{password}


        and graduated_school = #{graduatedSchool}


        and email = #{email}


        and role = #{role}


        and state = #{state}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and delete_time = #{deleteTime}
